async function postStatusUpdate(status) {
    const response = await fetch('https://your-api.com/status', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + accessToken
        },
        body: JSON.stringify({ status })
    });
    return response.json();
}
