import axios from 'axios';

export async function getStaticProps() {
    const res = await axios.get('http://localhost:1337/posts');
    const posts = res.data;
    return {
        props: { posts },
    };
}

const Blog = ({ posts }) => {
    return (
        <div>
            {posts.map(post => (
                <h2 key={post.id}>{post.title}</h2>
            ))}
        </div>
    );
};

export default Blog;

