"code": "async function startScreenShare() {
  try {
    const stream = await navigator.mediaDevices.getDisplayMedia({
      video: true, // Request video track
      audio: true  // Request audio track (optional, for system audio)
    });
    // 'stream' now contains the screen share video and optionally audio.
    // You can attach it to a video element or add to RTCPeerConnection.
    document.getElementById('localVideo').srcObject = stream;
    console.log('Screen share stream obtained:', stream);
    return stream;
  } catch (err) {
    console.error('Error starting screen share:', err);
    alert('Failed to start screen share. Please ensure permissions are granted.');
    return null;
  }
}"
