const axios = require('axios');

async function fetchSageData() {
    const response = await axios.get('https://api.sage.com/v1/customers', {
        headers: { 'Authorization': 'Bearer YOUR_ACCESS_TOKEN' }
    });
    return response.data;
}

const axios = require('axios');

async function createOdooRecord(data) {
    const response = await axios.post('https://your-odoo-instance.com/api/create', data);
    return response.data;
}
