def create_sales_order(session_id, order_data):
    headers = {'Content-Type': 'application/json'}
    response = requests.post(f'{URL}/api/sale.order', headers=headers, cookies={'session_id': session_id}, json=order_data)
    return response.json()

def sync_wp_users_to_odoo(wp_users):
    for user in wp_users:
        contact_data = {
            'name': user['display_name'],
            'email': user['user_email'],
            'phone': user['phone'],
        }
        create_sales_order(session_id, contact_data)
