async function updateInventory(productId, quantity) {
    const response = await fetch('https://your-odoo-instance.com/api/v1/product/' + productId, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer your_access_token'
        },
        body: JSON.stringify({ qty_available: quantity })
    });
    return response.json();
}
